let received_value = msg.payload.value;

if(Array.isArray(received_value)){
    received_value = received_value.length + " values"
}
else{
    received_value = Math.round(received_value * 100) / 100;
}

if (env.get("show_status")) {  
    node.status({ fill: "blue", shape: "dot", text: "Adding: " + received_value });
}
else {
    //
}

let chart_name = "";

if("chart_name" in msg){
    chart_name = msg.chart_name;
}
else{
    chart_name = env.get("chart_name");
}

return msg;
